INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857a4c8c50157a9020fb8001d', ' 蒂姆·罗宾斯 Tim Robbins   摩根·弗里曼 Morgan Freeman ', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 7, '/upload/filmPic/1476009214377.jpg', 1, '美国', 'f39c979857a4c8c50157a9002ff9001c', '肖申克的救赎1', '2018', '20世纪40年代末，小有成就的青年银行家安迪（蒂姆·罗宾斯 Tim Robbins 饰）因涉嫌杀害妻子及她的情人而锒铛入狱。在这座名为肖申克的监狱内，希望似乎虚无缥缈，终身监禁的惩罚无疑注定了安迪接下来灰暗绝望的人生。未过多久，安迪尝试接近囚犯中颇有声望的瑞德（摩根·弗里曼 Morgan Freeman 饰），请求对方帮自己搞来小锤子。以此为契机，二人逐渐熟稔，安迪也仿佛在鱼龙混杂、罪恶横生、黑白混淆的牢狱中找到属于自己的求生之道。他利用自身的专业知识，帮助监狱管理层逃税、洗黑钱，同时凭借与瑞德的交往在犯人中间也渐渐受到礼遇。表面看来，他已如瑞德那样对那堵高墙从憎恨转变为处之泰然，但是对自由的渴望仍促使他朝着心中的希望和目标前进。而关于其罪行的真相，似乎更使这一切朝前推进了一步……', '720', '全集', '科幻片', 'f39c979857b48a2a0157b83af6940044', '格斗片', 'f39c979857c72e810157c814e869005a', '2019-04-14 00:21:47.167', 1, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857a4c8c50157a91319d50023', '让·雷诺 Jean Reno', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 8, '/upload/filmPic/1476010343609.jpg', 1, '美国', 'f39c979857a4c8c50157a9002ff9001c', '这个杀手不太冷 Léon', '1994', '里昂（让·雷诺饰）是名孤独的职业杀手，受人雇佣。一天，邻居家小姑娘马蒂尔达（纳塔丽·波特曼饰)敲开他的房门，要求在他那里暂避杀身之祸。原来邻居家的主人是警方缉毒组的眼线，只因贪污了一小包毒品而遭恶警（加里·奥德曼饰）杀害全家的惩罚。马蒂尔达得到里昂的留救，幸免于难，并留在里昂那里。里昂教小女孩使枪，她教里昂法文，两人关系日趋亲密，相处融洽。                                                                     　　女孩想着去报仇，反倒被抓，里昂及时赶到，将女孩救回。混杂着哀怨情仇的正邪之战渐次升级，更大的冲突在所难免……', '1080', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '犯罪片', 'f39c979857a4c8c50157a8fe5ad8001b', '2019-04-06 15:24:29.231', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857a4c8c50157ac7abbbb002a', 'Juliana Harkavy  Joshua Mikel ， J·拉罗斯  Natalie Victoria  ', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 8, '/upload/filmPic/1476067503631.jpg', 1, '美国', 'f39c979857a4c8c50157a9002ff9001c', '最后一班1280超清迅雷下载', '2017', '一个菜鸟女警在自己职业生涯的首天被分配到一个即将关闭的警局独自执勤最后一晚，原本充满期待的工作变成了一个活生生的噩梦。。。', '1080', '全集', '恐怖片', 'f39c979857a4c8c50157ac76b5140027', '惊悚片', 'f39c979857a4c8c50157ac793ffc0029', '2019-04-06 21:49:56.746', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157ae4ea0890002', '张涵予、彭于晏、冯文娟', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 6, '/upload/filmPic/1476098164689.jpg', 1, '大陆', 'f39c979857ad7e8c0157ae4cbcd20001', '湄公河行动', '2016', '2011年10月5日，两艘中国商船在湄公河金三角水域遭遇袭击，13名中国船员全部遇难，泰国警方从船上搜出90万颗冰毒。消息传回国内，举国震惊。为了查明真相，云南缉毒总队长高刚（张涵予 饰）接受了特殊任务，率领一支骁勇善战的战斗小组进入泰国境内，与潜伏在泰国的情报员方新武（彭于晏 饰）碰头，二人联手深入金三角查案。经过调查，发现案件背后果然有着重重疑点，真正的凶手在嫁祸残害无辜中国船员后，不但逍遥法外，更意图利用毒品制造更大的阴谋……二人决定，不惜一切代价也要拿下真凶，打击毒品犯罪，为无辜国人讨回公道！', '480', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '犯罪片', 'f39c979857a4c8c50157a8fe5ad8001b', '2016-10-10 19:16:10.0', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157ae565bc60005', '乔尔·金纳曼 杰瑞德·莱托 威尔·史密斯', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 8, '/upload/filmPic/1476098666436.jpg', 1, '欧美', 'f39c979857a4c8c50157a9002ff9001c', '自杀小队', '2016', '“自杀小队”又称“X特遣队”，成员都是面对严重牢狱之灾的超级罪犯，包括死亡射手、鲨王、回旋镖队长和哈利·奎恩等，他们为美国政府干一些极其危险的脏活，以此来为自己减刑。每个成员脖子里都被装有纳米炸弹，如果不乖乖执行任务就会被炸死。 ', '480', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '犯罪片', 'f39c979857a4c8c50157a8fe5ad8001b', '2016-10-10 19:24:37', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157ae61551c000a', '朱智勋，金康宇，林智妍，千浩振', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 0, '/upload/filmPic/1476099392470.jpg', 1, '韩国', 'f39c979857ad7e8c0157ae5dd0450009', '奸臣', '2015', '朝鲜时代著名的暴君燕山君（金康宇饰）11年，身边奸臣当道，尤其是利用美色来让燕山沉迷而耽误政事的任士洪（千浩振饰）和任崇载（朱智勋饰）父子因为深知燕山君贪恋女色，因此决定从各个地方征集起各色美女前来奉送给他，亲自挑选了名妓丹熙（林智妍饰）并训练她摆在燕山君身边实施自己的奸计。', '1080', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '情色片', 'f39c979857ad7e8c0157ae5d8ce30008', '2016-10-10 19:36:36', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157ae707c4d000d', '张国荣  张丰毅  巩俐  英达  葛优  吕齐  马明威  蒋雯丽  吴大维 ', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 0, '/upload/filmPic/1476100375824.jpg', 1, '大陆', 'f39c979857ad7e8c0157ae4cbcd20001', '霸王别姬', '1994', '段小楼（张丰毅）与程蝶衣（张国荣）是一对打小一起长大的师兄弟，两人一个演生，一个饰旦，一向配合天衣无缝，尤其一出《霸王别姬》，更是誉满京城，为此，两人约定合演一辈子《霸王别姬》。但两人对戏剧与人生关系的理解有本质不同，段小楼深知戏非人生，程蝶衣则是人戏不分。 
　　段小楼在认为该成家立业之时迎娶了名妓菊仙（巩俐），致使程蝶衣认定菊仙是可耻的第三者，使段小楼做了叛徒，自此，三人围绕一出《霸王别姬》生出的爱恨情仇战开始随着时代风云的变迁不断升级，终酿成悲剧。', '1080', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '爱情片', 'f39c979857ad7e8c0157ae6ea77a000c', '2016-10-10 19:53:09', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157ae7406f0000f', '汤姆·汉克斯 / 罗宾·怀特 / 加里·西尼斯 / 麦凯尔泰·威廉逊 / 莎莉·菲尔德 /  海利·乔·奥斯蒙', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 0, '/upload/filmPic/1476100616236.jpg', 1, '欧美', 'f39c979857a4c8c50157a9002ff9001c', '阿甘正传', '1994', '阿甘（汤姆·汉克斯 饰）于二战结束后不久出生在美国南方阿拉巴马州一个闭塞的小镇，他先天弱智，智商只有75，然而他的妈妈是一个性格坚强的女性，她常常鼓励阿甘“傻人有傻福”，要他自强不息。 
　　阿甘像普通孩子一样上学，并且认识了一生的朋友和至爱珍妮（罗宾·莱特·潘 饰），在珍妮和妈妈的爱护下，阿甘凭着上帝赐予的“飞毛腿”开始了一生不停的奔跑。 
　　阿甘成为橄榄球巨星、越战英雄、乒乓球外交使者、亿万富翁，但是，他始终忘不了珍妮，几次匆匆的相聚和离别，更是加深了阿甘的思念。 
　　有一天，阿甘收到珍妮的信，他们终于又要见面……', '1080', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '爱情片', 'f39c979857ad7e8c0157ae6ea77a000c', '2016-10-10 19:57:02', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157ae8ad2c80012', ' 罗伯托·贝尼尼 / 尼可莱塔·布拉斯基 / 乔治·坎塔里尼 / 朱斯蒂诺·杜拉诺 / 塞尔吉奥·比尼·布斯特里克 / 玛丽莎·佩雷德斯 / 豪斯特·巴奇霍兹 / 朱利亚娜·洛约迪切', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 9, '/upload/filmPic/1476102109462.jpg', 1, '欧美', 'f39c979857a4c8c50157a9002ff9001c', '美丽人生', '1997', '犹太青年圭多（罗伯托·贝尼尼）邂逅美丽的女教师多拉（尼可莱塔·布拉斯基），他彬彬有礼的向多拉鞠躬：“早安！公主！”。历经诸多令人啼笑皆非的周折后，天遂人愿，两人幸福美满的生活在一起。 
　　然而好景不长，法西斯政权下，圭多和儿子被强行送往犹太人集中营。多拉虽没有犹太血统，毅然同行，与丈夫儿子分开关押在一个集中营里。聪明乐天的圭多哄骗儿子这只是一场游戏，奖品就是一辆大坦克，儿子快乐、天真的生活在纳粹的阴霾之中。尽管集中营的生活艰苦寂寞，圭多仍然带给他人很多快乐，他还趁机在纳粹的广播里问候妻子：“早安！公主！” 
　　法西斯政权即将倾覆，纳粹的集中营很快就要接受最后的清理，圭多编给儿子的游戏该怎么结束？他们一家能否平安的度过这黑暗的年代呢？', '1080', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '爱情片', 'f39c979857ad7e8c0157ae6ea77a000c', '2016-10-10 20:21:56', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857ad7e8c0157af419be90014', '孔侑 / 郑有美 / 马东锡 / 金秀安 / 金义城 / 崔宇植 / 安昭熙 / 沈恩京', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 5, '/upload/filmPic/1476114090331.jpg', 1, '韩国', 'f39c979857ad7e8c0157ae5dd0450009', '釜山行', '2016', '　　证券公司基金管理人石宇（孔侑 饰）光鲜精干，却也是个重利轻义之徒。妻子为此与之决裂，女儿秀安（金秀安 饰）则对如此自私的父亲越来越失望，决定前往釜山和母亲生活。在秀安生日这天，石宇抽出时间陪伴女儿登上开往釜山的特快列车。而与此同时，城市四处出现了极为可疑的暴动事件。政府极力洗白无法掩盖丧尸肆虐的事实，即便懵然无知的列车乘客也因为不速之客的到来而堕入恐慌绝望的地狱中。开车的刹那，一名感染者冲入车厢，而她很快尸变并对目光所及之处的健康人展开血腥屠杀。未过多久，丧尸便呈几何数爆发性地增长。石宇被迫和幸存者的乘客们在逼仄的空间中奋力求生。 
　　通往釜山的遥遥旅途布满杀机，危难时刻每个幸存者的人性也承受巨大的考验…… ', '1080', '全集', '恐怖片', 'f39c979857a4c8c50157ac76b5140027', '惊悚片', 'f39c979857a4c8c50157ac793ffc0029', '2016-10-10 23:41:35', 0, null, null, null, null);
