INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857b48a2a0157bdf32e97005a', ' 郑爽 / 杨洋 / 毛晓彤 / 白宇 / 秦语 / 宋欣佳怡 / 马春瑞 / 郑业成 / 张赫 / 牛骏峰 / 崔航 / 刘颖伦 / 陆妍淇 / 张彬彬', '电视剧', 'f39c979857a4c8c50157a8ea80700018', 8, '/upload/filmPic/1476360610746.jpg', 1, '大陆', 'f39c979857ad7e8c0157ae4cbcd20001', '微微一笑很倾城', '2016', '贝微微（郑爽 饰）是校园里的系花加学霸，而在虚拟的网络世界中，她是侠肝义胆仗剑江湖的红衣女侠“芦苇微微”。在游戏中，贝微微结识了“真水无香”，两人结为侠侣，没想到贝微微却因为不愿以真面目示人而遭到了真水无香的抛弃，在游戏中莫名其妙“被离婚”的贝微微之后又遇见了江湖高手“一笑奈何”，两人为了完成游戏任务而走到了一起。 
　　让贝微微没有想到的是，一笑奈何的线下身份，竟然是自己的同校师兄肖奈（杨洋 饰），肖奈是校园中的风云人物，篮球队的主力军，两人的情谊从线上发展到线下，一段跨越了虚拟与现实的感情就此拉开序幕。', '1080', '全集', '剧情片', 'f39c979857b48a2a0157b7b0974e0024', '爱情片', 'f39c979857b48a2a0157b7b0c3120025', '2016-10-13 20:10:13', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857b48a2a0157be61d1e3007d', '诹访部顺一 / 中原麻衣 / 鸟海浩辅 / 平川大辅 / 关智一 / 户松遥 / 小山力也 / 铃村健一 / 大原沙耶香 / 桧山修之 / 山路和弘 / 安元洋贵 / 大川透 / 田中敦子', '动漫', 'f39c979857a4c8c50157a8eaaee30019', 0, '/upload/filmPic/1476367860478.jpg', 1, '日本', 'f39c979857b48a2a0157be611374007c', 'Thunderbolt Fantasy 东离剑游纪 Thunderbolt Fantasy 東離劍遊紀（トウリケンユウキ） ', '2016', '曾经的一场人魔大战令两方人马都元气大伤，如今，战争的硝烟已然散去，却遗留下了诸多的难题要一一破解。人类为了赢得战争而锻造了大批拥有强大力量的武器，人们称之为“神诲魔械”，战争结束后，这批破坏力强大的武器的去向则成为了需要解决的首要问题，大家都同意将它们交由护印师来保管。 
　　丹衡（平川大辅 配音）和丹翡（中原麻衣 配音）兄妹两，身为护印师，担负起了保管最强兵器“天刑剑”的职责，却也给两人引来了杀身之祸。最终，天刑剑还是落入了蔑天骸（关智一 配音）的手中。为了追回魔剑，丹翡踏上了旅途，期间结识了凛雪鸦（鸟海浩辅 配音）和殇不患（诹访部顺一 配音）两人。', '1080', '第一季全集', '剧情片', 'f39c979857b48a2a0157be5f224f007a', '武侠片', 'f39c979857b48a2a0157be5f4bd0007b', '2016-10-13 22:11:04', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857b48a2a0157be8319d2008c', '赵炳锐 / 王姿允 / 刘波 / 余思潞 / 扎卡', '电视剧', 'f39c979857a4c8c50157a8ea80700018', 8, '/upload/filmPic/1476370031737.jpg', 1, '大陆', 'f39c979857ad7e8c0157ae4cbcd20001', '陈二狗的妖孽人生', '2016', '讲述了以陈二狗为主的底层群体辗转于大城市之间热血逐梦的励志故事。聚焦小人物，释放大能量。该剧着力于四大家族命运交织的传奇人生，勾勒出一幅怀揣英雄主义理想的小人物众生相。此次烽火戏诸侯也亲自操刀，担当本剧的艺术总监，对角色挑选、剧本改编、场景还原进行总体把控和监督，更有实力班底配置护航，电影级服化道加持，强强联合聚力精品打磨。一部刁民奋斗史，三代江湖恩仇录，“中国式江湖”即将拉开帷幕。 ', '1080', '第一季全集', '剧情片', 'f39c979857b48a2a0157b7b0974e0024', '爱情片', 'f39c979857b48a2a0157b7b0c3120025', '2016-10-13 22:47:25', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857b48a2a0157bebff9bf00a2', '理查·基尔 / 萨拉·罗默尔 / 琼·艾伦 / Robbie Sublett / 艾瑞克·阿瓦利 / 田川洋行 / 杰森·亚历山大 / 罗伯特·卡普荣', '电影', 'f39c979857a4874a0157a4a6a4fe0000', 0, '/upload/filmPic/1476374032629.jpg', 1, '欧美', 'f39c979857a4c8c50157a9002ff9001c', '忠犬八公的故事 Hachi: A Dog''s Tale', '2009', '八公（Forest 饰）是一条谜一样的犬，因为没有人知道它从哪里来。教授帕克（理查·基尔 Richard Gere 饰）在小镇的火车站拣到一只走失的小狗，冥冥中似乎注定小狗和帕克教授有着某种缘分，帕克一抱起这只小狗就再也放不下来，最终，帕克对小狗八公的疼爱感化了起初极力反对养狗的妻子卡特（琼·艾伦 Joan Allen 饰）。八公在帕克的呵护下慢慢长大，帕克上班时八公会一直把他送到车站，下班时八公也会早早便爬在车站等候，八公的忠诚让小镇的人家对它更加疼爱。有一天，八公在帕克要上班时表现异常，居然玩起了以往从来不会的捡球游戏，八公的表现让帕克非常满意，可是就是在那天，帕克因病去世。帕克的妻子、女儿安迪（萨拉·罗默尔 Sarah Roemer 饰）及女婿迈克尔（罗比·萨布莱特 Robbie Sublett 饰）怀着无比沉痛的心情埋葬了帕克，可是不明就里的八公却依然每天傍晚五点准时守候在小站的门前，等待着主人归来…… ', '1080', '全集', '剧情片', 'f39c979857a4c8c50157a8fe00ff001a', '家庭片', 'f39c979857b48a2a0157bebe075200a1', '2016-10-13 23:53:54', 0, null, null, null, null);
INSERT INTO video_resource_playback_site_sp_ly.t_bus_film (id, actor, cata_log_name, cata_log_id, evaluation, image, is_use, loc_name, loc_id, name, on_decade, plot, resolution, status, sub_class_name, sub_class_id, type_name, type_id, update_time, is_vip, create_by, create_time, update_by, remark) VALUES ('f39c979857b48a2a0157becd380800a4', '约翰尼·盖尔克奇 / 吉姆·帕森斯 / 凯莉·库柯 / 西蒙·赫尔伯格 / 昆瑙·内亚 / 梅丽莎·劳奇 / 马伊姆·拜力克 / 凯文·苏斯曼 / 劳拉·斯宾塞', '电视剧', 'f39c979857a4c8c50157a8ea80700018', 0, '/upload/filmPic/1476374899635.jpg', 1, '美国', 'f39c979857ad7e8c0157af44b2a40017', '生活大爆炸 第十季 The Big Bang Theory Season 10', '2016', '已经订婚的Leonard（约翰尼·盖尔克奇饰）和Penny（凯莉·库柯饰）终于决定要结婚了，他们不邀请任何亲朋好友，飞去拉斯维加斯快速举行婚礼。在路上Leonard向未婚妻坦白了一个秘密，这让他们之间产生了裂痕。长跑多年、即将结婚的Leonard和Penny能否顺利完婚。另一方面，Sheldon（吉姆·帕森斯饰）遭遇了重大打击——女朋友Amy向他提出分手。他多次试图挽回，然而Amy对此相当坚决。Howard（西蒙·赫尔伯格饰）和Raj等老友也为他们的事而伤神。', '1080', '第十季第四集', '剧情片', 'f39c979857b48a2a0157b7b0974e0024', '爱情片', 'f39c979857b48a2a0157b7b0c3120025', '2016-10-14 00:08:22', 0, null, null, null, null);
